var script_f23_25_23964=[""].join("\n");
var outline_f23_25_23964=null;
var title_f23_25_23965="Drug regimens for tuberculosis";
var content_f23_25_23965=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50102&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50102&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drug regimens for active tuberculosis in adults caused by drug-susceptible organisms",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\" colspan=\"2\">",
"       Initial phase",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"2\">",
"       Continuation phase",
"      </td>",
"      <td class=\"subtitle1\" rowspan=\"2\">",
"       <p>",
"        Range of total doses",
"       </p>",
"       <p>",
"        (minimal duration)",
"       </p>",
"      </td>",
"      <td class=\"subtitle1\" rowspan=\"2\">",
"       Rating*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       Drugs",
"      </td>",
"      <td class=\"subtitle2\">",
"       Interval and doses&bull; (minimal duration)",
"      </td>",
"      <td class=\"subtitle2\">",
"       Drugs",
"      </td>",
"      <td class=\"subtitle2\">",
"       Interval and doses&bull; &Delta; (minimal duration)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"6\">",
"       Regimen 1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"3\">",
"       <p>",
"        INH",
"       </p>",
"       <p>",
"        RIF",
"       </p>",
"       <p>",
"        PZA",
"       </p>",
"       <p>",
"        EMB",
"       </p>",
"      </td>",
"      <td rowspan=\"3\">",
"       <p>",
"        Seven days per week for 56 doses (8 wk)",
"       </p>",
"       <p>",
"        or 5 d/wk for 40 doses (8 wk)&loz;",
"       </p>",
"      </td>",
"      <td>",
"       1a. INH/RIF",
"      </td>",
"      <td>",
"       <p>",
"        Seven days per week for 126 doses (18 wk)",
"       </p>",
"       <p>",
"        or 5 d/wk for 90 doses (18 wk)&loz;",
"       </p>",
"      </td>",
"      <td>",
"       182-130 (26 wk)",
"      </td>",
"      <td>",
"       A",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1b. INH/RIF",
"      </td>",
"      <td>",
"       Twice weekly for 36 doses (18 wk)",
"      </td>",
"      <td>",
"       92-76 (26 wk)",
"      </td>",
"      <td>",
"       A",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1c. INH/RPT&sect;",
"      </td>",
"      <td>",
"       Once weekly for 18 doses (18 wk)",
"      </td>",
"      <td>",
"       74-58 (26 wk)",
"      </td>",
"      <td>",
"       B",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"6\">",
"       Regimen 2",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        INH",
"       </p>",
"       <p>",
"        RIF",
"       </p>",
"       <p>",
"        PZA",
"       </p>",
"       <p>",
"        EMB",
"       </p>",
"      </td>",
"      <td rowspan=\"2\">",
"       <p>",
"        Seven days per week for 14 doses (2 wk), then twice weekly for 12 doses (6 wk) or",
"       </p>",
"       <p>",
"        5 d/wk for 10 doses (2 wk)&loz;, then twice weekly for 12 doses (6 wk)",
"       </p>",
"      </td>",
"      <td>",
"       2a. INH/RIF",
"      </td>",
"      <td>",
"       Twice weekly for 36 doses (18 wk)",
"      </td>",
"      <td>",
"       62-58 (26 wk)",
"      </td>",
"      <td>",
"       A",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2b. INH/RPT&sect;",
"      </td>",
"      <td>",
"       Once weekly for 18 doses (18 wk)",
"      </td>",
"      <td>",
"       44-40",
"      </td>",
"      <td>",
"       B",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"6\">",
"       Regimen 3",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <p>",
"        INH",
"       </p>",
"       <p>",
"        RIF",
"       </p>",
"       <p>",
"        PZA",
"       </p>",
"       <p>",
"        EMB",
"       </p>",
"      </td>",
"      <td>",
"       Three times weekly for 24 doses (8 wk)",
"      </td>",
"      <td>",
"       3a. INH/RIF",
"      </td>",
"      <td>",
"       Three times weekly for 54 doses (18 wk)",
"      </td>",
"      <td>",
"       78 (26 wk)",
"      </td>",
"      <td>",
"       B",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"6\">",
"       Regimen 4",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       <p>",
"        INH",
"       </p>",
"       <p>",
"        RIF",
"       </p>",
"       <p>",
"        EMB",
"       </p>",
"      </td>",
"      <td rowspan=\"2\">",
"       <p>",
"        Seven days per week for 56 doses (8 wk)",
"       </p>",
"       <p>",
"        or 5 d/wk for 40 doses (8 wk)&loz;",
"       </p>",
"      </td>",
"      <td>",
"       4a. INH/RIF",
"      </td>",
"      <td>",
"       <p>",
"        Seven days per week for 217 doses (31 wk)",
"       </p>",
"       <p>",
"        or 5 d/wk for 155 doses (31 wk)&loz;",
"       </p>",
"      </td>",
"      <td>",
"       273-195 (39 wk)",
"      </td>",
"      <td>",
"       C",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4b. INH/RIF",
"      </td>",
"      <td>",
"       Twice weekly for 62 doses (31 wk)",
"      </td>",
"      <td>",
"       118-102 (39 wk)",
"      </td>",
"      <td>",
"       C",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     EMB: ethambutol; INH: isoniazid; PZA: pyrazinamide; RIF: rifampin; RPT: rifapentine.",
"     <br>",
"      * Definitions of ratings: A: preferred; B: acceptable alternative; C: offer when A and B cannot be given.",
"      <br>",
"       &bull; When DOT is used, drugs may be given 5 days/week and the necessary number of doses adjusted accordingly. Although there are no studies that compare five with seven daily doses, extensive experience indicates this would be an effective practice.",
"       <br>",
"        &Delta; Patients with cavitation on initial chest radiograph and positive cultures at completion of 2 months of therapy should receive a 7-month (31-week; either 217 doses [daily] or 62 doses [twice weekly]) continuation phase.",
"        <br>",
"         &loz; Five-day-a-week administration is always given by DOT.",
"         <br>",
"          &sect; Options 1c and 2b should be used only in HIV-negative patients who have negative sputum smears at the time of completion of 2 months of therapy and who do not have cavitation on the initial chest radiograph (see text). For patients started on this regimen and found to have a positive culture from the two month specimen, treatment should be extended an extra three months.",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from Am J Respir Crit Care Med 2003; 167:603.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
