var content_f23_3_23612=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F62762&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F62762&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Medical therapy of arrhythmias associated with Wolff-Parkinson-White syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Arrhythmia",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preferred",
"       </td>",
"       <td class=\"subtitle1\">",
"        Contraindicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Orthodromic",
"AV reentrant tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute termination*",
"       </td>",
"       <td>",
"        Vagal maneuvers",
"        <br/>",
"        IV verapamil",
"        <br/>",
"        IV adenosine",
"        <br/>",
"        <br/>",
"        <strong>",
"         Second-line:",
"        </strong>",
"        <br/>",
"        IV beta blocker",
"        <br/>",
"        IV digoxin",
"        <br/>",
"        IV amiodarone",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic prevention&bull;",
"       </td>",
"       <td>",
"        Class IC",
"        <br/>",
"        <br/>",
"        <strong>",
"         Second-line:",
"        </strong>",
"        <br/>",
"        Beta blocker",
"        <br/>",
"        Calcium channel blocker",
"        <br/>",
"        Digoxin",
"        <br/>",
"        Class IA",
"        <br/>",
"        Amiodarone",
"        <br/>",
"        Sotalol",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Antidromic AV reentrant",
"tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute termination*",
"       </td>",
"       <td>",
"        IV procainamide",
"       </td>",
"       <td>",
"        <strong>",
"         Consider only if diagnosis",
"secure:",
"        </strong>",
"        <br/>",
"        IV beta blocker",
"        <br/>",
"        IV calcium channel blocker",
"        <br/>",
"        IV digoxin",
"        <br/>",
"        IV adenosine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic prevention&bull;",
"       </td>",
"       <td>",
"        Class IC",
"        <br/>",
"        <br/>",
"        <strong>",
"         Second-line:",
"        </strong>",
"        <br/>",
"        Class IA",
"        <br/>",
"        Amiodarone",
"       </td>",
"       <td>",
"        Beta blocker",
"        <br/>",
"        Calcium",
"channel blocker",
"        <br/>",
"        Digoxin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Atrial fibrillation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acute termination*",
"       </td>",
"       <td colspan=\"2\">",
"        IV procainamide",
"        <br/>",
"        IV class IC if available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic prevention&bull;",
"       </td>",
"       <td colspan=\"2\">",
"        Class IC",
"        <br/>",
"        Class IA",
"        <br/>",
"        Amiodarone",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Class IC: flecainide, propafenone; class IA: quinidine, procainamide, disopyramide.",
"     <br>",
"      * Cardioversion is indicated if hemodynamically unstable or drugs are ineffective.",
"      <br>",
"       &bull; Ablation of the accessory pathway is generally preferred to cure the arrhythmia.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
