" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Selection of patients for allogeneic hematopoietic cell transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Good risk candidates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Young age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No comorbid conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No active infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lymphohematopoietic disease in remission or responsive to therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Good socioeconomic support system",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HLA-matched donor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low risk of post-transplantation relapse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        High risk candidates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Older age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Comorbid conditions present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refractory/relapsed disease (diagnosis-dependent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aggressive prior therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High risk/complex karyotype",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
