var title_f23_38_24172="Tips for specific patient impairments";
var content_f23_38_24172=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F58401&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F58401&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tips to address specific impairments that may impede patient-physician communication",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Impairment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Helpful material, resources, or personnel",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Hearing",
"       </td>",
"       <td>",
"        <p>",
"         Ask the patient: \"Can you hear me okay the way I'm speaking now?\"",
"        </p>",
"        <p>",
"         Speak slowly and clearly, but don't over-exaggerate words.",
"        </p>",
"        <p>",
"         Face the patient to allow lip-reading.",
"        </p>",
"        <p>",
"         As you increase the volume of your voice, try to keep pitch low (higher frequencies are more impaired in presbycusis).",
"        </p>",
"        <p>",
"         Minimize ambient noise.",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Portable amplifiers*",
"        </p>",
"        <p>",
"         Written instructions or educational material",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Vision",
"       </td>",
"       <td>",
"        <p>",
"         Avoid communication that relies on gestures: \"Place your urine sample over there\" or \"Can you do this with your hands?\"",
"        </p>",
"        <p>",
"         Be aware that the patient may miss subtle body language or facial expressions (a smile or an empathic nod). Use words to share those emotions.",
"        </p>",
"        <p>",
"         If you offer a hand, tell the patient so he/she can return the gesture. Likewise, tell the patient when you enter and leave the room.",
"        </p>",
"        <p>",
"         If there are multiple people in the room, speak the patient's name to signify that you are addressing him/her.",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Large-print forms and patient education material",
"        </p>",
"        <p>",
"         Reading glasses/magnifiers available",
"        </p>",
"        <p>",
"         Audio-recorded information or personnel to read consent forms or other materials",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cognitive",
"       </td>",
"       <td>",
"        <p>",
"         Stick to the point.",
"        </p>",
"        <p>",
"         When questions are repeated, answer them again, just as you did the first time.",
"        </p>",
"        <p>",
"         Involve caregiver or companion when available and appropriate, but avoid excluding the patient.",
"        </p>",
"        <p>",
"         Write down key instructions and findings for the patient to take home and refer to later.",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Take-home education materials",
"        </p>",
"        <p>",
"         Nurses or other extended care providers to reinforce teaching and assess patient's understanding on issues discussed",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If an amplifier is unavailable, you can place the ear pieces of your stethoscope into the patient's ears and speak into the bell.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
