" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Sedatives and hypnotics abuse and dependence: Pharmacology and epidemiology",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/42/24232/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/42/24232/contributors\">",
"     Robert L DuPont, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/42/24232/contributors\">",
"     William Greene, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/42/24232/contributors\">",
"     R Bruce Lydiard, PhD, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/42/24232/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/42/24232/contributors\">",
"     Murray B Stein, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
