var title_f23_42_24236="Comparison of reconstructive materials";
var content_f23_42_24236=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F72868&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F72868&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of reconstructive materials",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Graft type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Synthetic",
"       </td>",
"       <td>",
"        <p>",
"         Accessible: easy to manufacture and obtain",
"        </p>",
"        <p>",
"         Consistent: materials are reproducible",
"        </p>",
"        <p>",
"         Durable",
"        </p>",
"        <p>",
"         Cost-effective: less expensive to produce than allo- and xenografts",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Infection: microporous and multifilament grafts in particular",
"        </p>",
"        <p>",
"         Erosion: may erode into vagina or viscus",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Autograft",
"       </td>",
"       <td>",
"        <p>",
"         Host response: no risk of rejection",
"        </p>",
"        <p>",
"         No erosion risk: although permanent sutures used to secure may erode",
"        </p>",
"        <p>",
"         Cost-effective",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Limited tissue quantity and quality possible",
"        </p>",
"        <p>",
"         Morbidity: tissue harvest required",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Allograft",
"       </td>",
"       <td>",
"        <p>",
"         Accessible: larger quantities available than autografts, but depends on donor supply",
"        </p>",
"        <p>",
"         No harvest morbidity",
"        </p>",
"        <p>",
"         Low erosion risk",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Infection: theoretical risk of transmission of host pathogens",
"        </p>",
"        <p>",
"         Host response: potential for rejection",
"        </p>",
"        <p>",
"         Inconsistent graft strength",
"        </p>",
"        <p>",
"         Limited durability: processing may weaken material",
"        </p>",
"        <p>",
"         Limited ability to compare among materials",
"        </p>",
"        <p>",
"         Expensive",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Xenograft",
"       </td>",
"       <td>",
"        <p>",
"         Accessible: larger quantities available than autografts",
"        </p>",
"        <p>",
"         No harvest morbidity",
"        </p>",
"        <p>",
"         Low erosion risk",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Infection: theoretical risk of transmission of host pathogens",
"        </p>",
"        <p>",
"         Host response: potential for rejection given nonhuman source",
"        </p>",
"        <p>",
"         Inconsistent graft strength",
"        </p>",
"        <p>",
"         Limited durability: processing may weaken material",
"        </p>",
"        <p>",
"         Cultural issues with porcine or bovine products",
"        </p>",
"        <p>",
"         Expensive",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f23_42_24236=[""].join("\n");
var outline_f23_42_24236=null;
var title_f23_42_24237="DKA rapid overview";
var content_f23_42_24237=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F57723&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F57723&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Emergent DKA management in adults: rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Clinical features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       DKA usually evolves rapidly over a 24-hour period.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Common, early signs of ketoacidosis include nausea, vomiting, abdominal pain, and hyperventilation. The earliest symptoms of marked hyperglycemia are polyuria, polydipsia, and weight loss.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       As hyperglycemia worsens, neurologic symptoms appear, and may progress to include lethargy, focal deficits, obtundation, seizure, and coma.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Common causes of DKA include: infection; noncompliance, inappropriate adjustment, or cessation of insulin; new onset diabetes mellitus; and myocardial ischemia.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Evaluation and laboratory findings",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Assess vital signs, cardiorespiratory status, and mental status.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Assess volume status: vital signs, skin turgor, mucosa, urine output.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Obtain the following studies: serum glucose, urinalysis and urine ketones, serum electrolytes, BUN and creatinine, plasma osmolality, mixed venous blood gas, electrocardiogram; add serum ketones if urine ketones present.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diabetic ketoacidosis (DKA) is characterized by hyperglycemia, an elevated anion gap metabolic acidosis, and ketonemia. Dehydration and potassium deficits are often severe.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Serum glucose is usually greater than 250 mg/dL (13.9 mmol/L) and less than 800 mg/dL (44.4 mmol/L). In certain instances (eg, insulin given prior to ED arrival), the glucose may be only mildly elevated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Additional testing is obtained based on clinical circumstances and may include: blood or urine cultures, lipase, chest x-ray.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Management",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stabilize the patient's airway, breathing, and circulation.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Obtain large bore IV (&ge;16 gauge) access; monitor using a cardiac monitor, capnography, and pulse oximetry.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Monitor serum glucose hourly, and basic electrolytes, plasma osmolality, and venous pH every two to four hours until the patient is stable.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Determine and treat any underlying cause of DKA (eg, pneumonia or urinary infection, myocardial ischemia).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Replete fluid deficits:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Give several liters of isotonic (0.9 percent) saline as rapidly as possible to patients with signs of shock.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Give isotonic saline at 15 to 20 mL/kg per hour, in the absence of cardiac compromise, for the first few hours to hypovolemic patients without shock.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; After intravascular volume is restored, give one-half isotonic (0.45 percent) saline at 4 to 14 mL/kg per hour if the corrected serum sodium is normal or elevated; isotonic saline is continued if the corrected serum sodium is reduced.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Add dextrose to the saline solution when the serum glucose reaches 200 mg/dL (11.1 mmol/L).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Replete potassium (K+) deficits:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Regardless of the initial measured serum potassium, patients with DKA have a large total body potassium deficit.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; If initial serum K+ is below 3.3 mEq/L, hold insulin and give K+ 20 to 30 mEq/hour IV until K+ concentration is above 3.3 mEq/L.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; If initial serum K+ is between 3.3 and 5.3 mEq/L, give K+ 20 to 30 mEq per liter IV fluid; maintain K+ between 4 to 5 mEq/L.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; If initial serum K+ is above 5.3 mEq/L do not give K+; check K+ every 2 hours.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Give insulin:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Do not give insulin if initial serum K+ is below 3.3 mEq/L; replete K+ first.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Give all patients without a serum K+ below 3.3 mEq/L regular insulin. Either of two regimens can be used: 0.1 units/kg IV bolus, then start a continuous IV infusion 0.1 units/kg per hour; OR do not give bolus and start a continuous IV infusion at a rate of 0.14 units/kg per hour.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; Continue insulin infusion until ketoacidosis is resolved, serum glucose is below 200 mg/dL (11.1 mmol/L), and subcutaneous insulin is begun.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Give sodium bicarbonate to patients with pH below 7.00:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; If the arterial pH is between 6.90 and 7.00, give 50 meq of sodium bicarbonate plus 10 meq of potassium chloride in 200 mL of sterile water over two hours.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       &bull; If the arterial pH is below 6.90, give 100 meq of sodium bicarbonate plus 20 meq of potassium chloride in 400 mL sterile water over two hours.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
