" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Neurologic complications of bacterial meningitis in the neonate",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Morven S Edwards, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Carol J Baker, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Sheldon L Kaplan, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Leonard E Weisman, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/43/24246/contributors\">",
"     Douglas R Nordli, Jr, MD",
"    </a>",
"    <br/>",
"   </div>",
