" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Bites of recluse spiders",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/43/24248/contributors\">",
