var title_f23_45_24285="Antibiotic doses for pneumococcal pneumonia in children";
var content_f23_45_24285=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F59924&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F59924&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Doses of antimicrobials for the treatment of pneumococcal pneumonia in children older than three months",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Parenteral therapy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Oral therapy (step-down therapy or mild infection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        <em>",
"         Streptococcus pneumoniae",
"        </em>",
"        susceptible to penicillin (MIC &le;2 mcg/mL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Preferred regimens",
"       </td>",
"       <td>",
"        Ampicillin 150 to 200 mg/kg per day divided every 6 hours (maximum daily dose 6 g)",
"       </td>",
"       <td>",
"        Amoxicillin 90 mg/kg per day in 2 doses or 45 mg/kg per day in 3 doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Penicillin 200,000 to 250,000 units/kg per day divided every 4 or 6 hours (maximum daily dose 24 million units)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"5\">",
"        Alternative regimens",
"       </td>",
"       <td>",
"        Ceftriaxone* 50 to 100 mg/kg per day divided every 12 or 24 hours (maximum daily dose 4 g)",
"       </td>",
"       <td>",
"        Cefpodoxime 10 mg/kg per day divided in 2 doses (maximum daily dose 400 mg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cefotaxime 150 mg/kg per day divided every 8 hours (maximum daily dose 10 g)",
"       </td>",
"       <td>",
"        Cefuroxime 20 to 30 mg/kg per day divided in 2 doses (maximum daily dose 2 g)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin 40 mg/kg per day divided every 6 or 8 hours (maximum daily dose 3.6 g)",
"       </td>",
"       <td>",
"        Cefprozil 15 to 30 mg/kg per day in 2 doses (maximum daily dose 1g)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vancomycin 40 to 60 mg/kg per day divided every 6 or 8 hours (maximum daily dose 4 g)",
"       </td>",
"       <td>",
"        <p>",
"         Levofloxacin (if susceptible)",
"        </p>",
"        <p>",
"         Age 6 months to 5 years: 16 to 20 mg/kg per day divided in 2 doses (maximum daily dose 750 mg)",
"        </p>",
"        <p>",
"         Age 5 to 16 years: 8 to 10 mg/kg once daily (maximum daily dose 750 mg)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Linezolid",
"        </p>",
"        <p>",
"         Age &lt;12 years: 30 mg/kg per day divided in 3 doses (maximum daily dose 1800 mg)",
"        </p>",
"        <p>",
"         Age &ge;12 years: 20 mg/kg per day divided in 2 doses (maximum daily dose 1200 mg)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        <em>",
"         S. pneumoniae",
"        </em>",
"        resistant to penicillin (MIC &ge;4 mcg/mL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Preferred regimens",
"       </td>",
"       <td>",
"        Ceftriaxone 100 mg/kg per day divided every 12 or 24 hours (maximum daily dose 4 g)",
"       </td>",
"       <td>",
"        <p>",
"         Levofloxacin (if susceptible)",
"        </p>",
"        <p>",
"         Age 6 months to 5 years: 16 to 20 mg/kg per day divided in 2 doses (maximum daily dose 750 mg)",
"        </p>",
"        <p>",
"         Age 5 to 16 years: 8 to 10 mg/kg once daily (maximum daily dose 750 mg)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Linezolid",
"        </p>",
"        <p>",
"         Age &lt;12 years: 30 mg/kg per day divided in 3 doses (maximum daily dose 1800 mg)",
"        </p>",
"        <p>",
"         Age &ge;12 years: 20 mg/kg per day in 2 doses (maximum daily dose 1200 mg)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"5\">",
"        Alternative regimens",
"       </td>",
"       <td>",
"        Ampicillin 300 to 400 mg/kg per day divided every 6 hours (maximum daily dose 6 g)",
"       </td>",
"       <td>",
"        Clindamycin",
"        <sup>",
"         &bull;",
"        </sup>",
"        40 mg/kg per day divided in 2 doses (maximum daily dose 1.8 g)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Levofloxacin",
"        </p>",
"        <p>",
"         Age 6 months to 5 years: 16 to 20 mg/kg per day divided every 12 hours (maximum daily dose 750 mg)",
"        </p>",
"        <p>",
"         Age 5 to 16 years: 8 or 10 mg/kg once daily (maximum daily dose 750 mg)",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Linezolid",
"        </p>",
"        <p>",
"         Age &lt;12 years: 30 mg/kg per day divided every 8 hours (maximum daily dose 1800 mg)",
"        </p>",
"        <p>",
"         Age &ge;12 years: 20 mg/kg per day divided every 12 hours (maximum daily dose 1200 mg)",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin",
"        <sup>",
"         &bull;",
"        </sup>",
"        40 mg/kg per day divided every 6 or 8 hours (maximum daily dose 3.6 g)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vancomycin 40 to 60 mg/kg per day divided every 6 or 8 hours (maximum daily dose 4 g)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Preferred for parenteral outpatient therapy.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Clindamycin resistance appears to be increasing in certain geographic areas among",
"     <em>",
"      S. pneumoniae",
"     </em>",
"     infections.",
"    </div>",
"    <div class=\"reference\">",
"     From: Bradley JS, Byington CL, Shah SS, et al. The management of community-acquired pneumonia in infants and children older than 3 months of age: clinical practice guidelines by the Pediatric Infectious Diseases Society and the Infectious Diseases Society of America. Clin Infect Dis 2011; 53:e25, with permission of the Infectious Diseases Society of America. Copyright &copy; 2011 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
