var title_f23_49_24349="Disorders causing visual hallucinations";
var content_f23_49_24349=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F73106&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F73106&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Disorders causing visual hallucinations",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Etiology",
"      </td>",
"      <td class=\"subtitle1\">",
"       Classification and characteristic features",
"      </td>",
"      <td class=\"subtitle1\">",
"       Location",
"      </td>",
"      <td class=\"subtitle1\">",
"       Triggers",
"      </td>",
"      <td class=\"subtitle1\">",
"       Duration",
"      </td>",
"      <td class=\"subtitle1\">",
"       Frequency",
"      </td>",
"      <td class=\"subtitle1\">",
"       Insight",
"      </td>",
"      <td class=\"subtitle1\">",
"       Associated symptoms",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Retinal pathology",
"      </td>",
"      <td>",
"       <p>",
"        Simple",
"       </p>",
"       <p>",
"        Intrinsic motion is common; eg, flashing lights",
"       </p>",
"      </td>",
"      <td>",
"       Monocular or binocular, depending on underlying pathology",
"      </td>",
"      <td>",
"       Valsalva if due to posterior vitreous detachment",
"      </td>",
"      <td>",
"       Seconds",
"      </td>",
"      <td>",
"       Variable",
"      </td>",
"      <td>",
"       Intact",
"      </td>",
"      <td>",
"       Possible vision loss per abnormal funduscopic examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Vision loss, release hallucinations",
"      </td>",
"      <td>",
"       <p>",
"        Simple or complex",
"       </p>",
"       <p>",
"        Can be stationary, have intrinsic motion, or move en bloc",
"       </p>",
"      </td>",
"      <td>",
"       Monocular or binocular, full field or hemifield depending on underlying pathology",
"      </td>",
"      <td>",
"       Sensory deprivation, decreased arousal",
"      </td>",
"      <td>",
"       Variable",
"      </td>",
"      <td>",
"       Frequent, at least weekly, up to multiple times in one day",
"      </td>",
"      <td>",
"       Often intact",
"      </td>",
"      <td>",
"       Related to underlying condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Migraine",
"      </td>",
"      <td>",
"       <p>",
"        Usually simple and geometric in form; eg, fortification spectra, scintillating scotoma",
"       </p>",
"       <p>",
"        Spread or movement across the visual field over minutes is characteristic",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Binocular, usually hemifield",
"       </p>",
"       <p>",
"        Often start centrally and move to periphery",
"       </p>",
"      </td>",
"      <td>",
"       Migraine triggers (eg, lack of sleep, red wine, menses)",
"      </td>",
"      <td>",
"       Several minutes to an hour",
"      </td>",
"      <td>",
"       Variable, usually less often than weekly",
"      </td>",
"      <td>",
"       Intact",
"      </td>",
"      <td>",
"       Migraine headache, often with nausea, vomiting, photophobia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Seizures",
"      </td>",
"      <td>",
"       <p>",
"        Simple more common than complex",
"       </p>",
"       <p>",
"        Typically circular and colored",
"       </p>",
"       <p>",
"        Movement across the field is rapid (seconds)",
"       </p>",
"      </td>",
"      <td>",
"       Binocular, hemifield",
"      </td>",
"      <td>",
"       Usually occur without trigger",
"      </td>",
"      <td>",
"       Seconds to 1-2 minutes",
"      </td>",
"      <td>",
"       Variable",
"      </td>",
"      <td>",
"       Usually intact",
"      </td>",
"      <td>",
"       Other ictal phenomena (eg, automatisms, deja vu, convulsions), postictal headache common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Dementia with Lewy bodies, Parkinson disease",
"      </td>",
"      <td>",
"       Simple or complex",
"      </td>",
"      <td>",
"       Binocular, full field",
"      </td>",
"      <td>",
"       Usually no trigger",
"      </td>",
"      <td>",
"       Variable, usually minutes",
"      </td>",
"      <td>",
"       Variable, can be daily",
"      </td>",
"      <td>",
"       Variable, perhaps related to cognitive status",
"      </td>",
"      <td>",
"       Dementia, Parkinsonism",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Alcohol withdrawal",
"      </td>",
"      <td>",
"       Complex, often associated with auditory and tactile hallucinations",
"      </td>",
"      <td>",
"       Binocular, full field",
"      </td>",
"      <td>",
"       Abstinence",
"      </td>",
"      <td>",
"       Persistent",
"      </td>",
"      <td>",
"       Persist during withdrawal",
"      </td>",
"      <td>",
"       Often impaired",
"      </td>",
"      <td>",
"       Autonomic disturbances, confusion, agitation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Peduncular hallucinosis",
"      </td>",
"      <td>",
"       Complex, often associated with auditory and tactile hallucinations",
"      </td>",
"      <td>",
"       Binocular, full field",
"      </td>",
"      <td>",
"       More common in evening hours",
"      </td>",
"      <td>",
"       Variable may persist",
"      </td>",
"      <td>",
"       Variable",
"      </td>",
"      <td>",
"       Maybe impaired",
"      </td>",
"      <td>",
"       Sleep disturbances, other brainstem or diencephalic signs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Narcolepsy",
"      </td>",
"      <td>",
"       Complex, often associated with auditory and tactile hallucinations",
"      </td>",
"      <td>",
"       Binocular, full field",
"      </td>",
"      <td>",
"       Usually on falling to or on awakening from sleep",
"      </td>",
"      <td>",
"       Variable, seconds to minutes",
"      </td>",
"      <td>",
"       Often nightly",
"      </td>",
"      <td>",
"       Usually intact",
"      </td>",
"      <td>",
"       Cataplexy, excessive daytime sleepiness, sleep paralysis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\">",
"       Psychiatric illness",
"      </td>",
"      <td>",
"       Complex, often associated with auditory and tactile hallucinations",
"      </td>",
"      <td>",
"       Binocular, full field",
"      </td>",
"      <td>",
"       No trigger",
"      </td>",
"      <td>",
"       Variable",
"      </td>",
"      <td>",
"       Frequent",
"      </td>",
"      <td>",
"       Usually absent",
"      </td>",
"      <td>",
"       Disordered thoughts, delusions",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
