var title_f23_5_23644="Dentin dysplasia";
var content_f23_5_23644=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F77566&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F77566&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Heritable conditions of dentin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Molecular defect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dentinogenesis imperfecta type 1 (occurs in some forms of osteogenesis imperfecta)",
"        <br/>",
"        (OMIM #166200)",
"       </td>",
"       <td>",
"        Variable blue-gray to yellow-brown teeth, enamel fracturing, excessive wear, primary teeth usually more affected than permanent",
"       </td>",
"       <td>",
"        Variable pulp obliteration, bulbous crowns, altered root morphology, increased risk for dentigerous cysts",
"       </td>",
"       <td>",
"        Mutations of collagen type 1 genes (",
"        <em>",
"         COL1A1",
"        </em>",
"        and",
"        <em>",
"         COLl1A2",
"        </em>",
"        )",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dentinogenesis imperfecta type 2",
"        <br/>",
"        (OMIM #125490)",
"       </td>",
"       <td>",
"        Same appearance and variability as in DI type 1, often similar severity in primary and permanent dentitions",
"       </td>",
"       <td>",
"        Pulp chamber obliteration that can begin before tooth eruption, abnormal crown and root morphology",
"       </td>",
"       <td>",
"        Unknown, linked to 4q21 locus",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dentinogenesis imperfecta type 3",
"        <br/>",
"        (OMIM #125500)",
"       </td>",
"       <td>",
"        Similar clinical phenotype as DI types 1 and 2 although typically severe expression with enamel loss and extensive wear occurring early",
"       </td>",
"       <td>",
"        Large pulp chambers, very thin dentin, bulbous crowns, and diminished root structure",
"       </td>",
"       <td>",
"        Unknown, linked to 4q2l locus",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dentin dysplasia type 1",
"        <br/>",
"        (OMIM #125400)",
"       </td>",
"       <td>",
"        Normal clinical crown morphology and coloration in primary and permanent dentitions, malaligned teeth, frequent dental abscess",
"       </td>",
"       <td>",
"        Pulp obliteration and short blunt roots in both primary and permanent dentitions",
"       </td>",
"       <td>",
"        Unknown, no locus identified",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dentin dysplasia type 2",
"        <br/>",
"        (OMIM #125420)",
"       </td>",
"       <td>",
"        Primary dentition has same phenotype as DI, permanent dentition has normal to slight blue-gray discoloration",
"       </td>",
"       <td>",
"        Pulp obliteration in primary dentition, abnormal pulp morphology, and pulp stones in permanent dentition",
"       </td>",
"       <td>",
"        Unknown, linked to 4q2l locus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     DI: dentinogenesis imperfecta.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Wright JT. Pediatr Clin North Am 2000; 47:986. Copyright 2000 W.B. Saunders Company.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
