" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Exanthematous (morbilliform) drug eruption",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/50/24360/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/50/24360/contributors\">",
"     Andreas J Bircher, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/50/24360/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
