" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common causes of syncope",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Noncardiovascular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Reflex mechanisms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Vasovagal and vasodepressor syncope (neurocardiogenic syncope)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Micturition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Deglutition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cough",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Orthostatic hypotension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dysautonomias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Fluid depletion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Illness, bedrest, deconditioning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Drugs - antidepressants, sympathetic blockers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Psychogenic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hysterical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Panic disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anxiety disorder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Undiagnosed seizures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Improperly diagnosed syncope - confusional states, eg, due to hypoglycemia, stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug-induced loss of consciousness (consider alcohol, illicit drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cardiovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Arrhythmic causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        AV block with bradycardia (structural changes, drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sinus pauses/bradycardia (vagal causes, sick sinus syndrome, negative chronotropic drugs such as beta blockers and calcium channel blockers)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ventricular tachycardia due to structural heart disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Nonarrhythmic causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypertrophic cardiomyopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Aortic stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Syncope of unknown origin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        About 50 percent of patients presenting to the hospital",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
