var title_f23_53_24413="X-linked adrenoleukodystrophy A";
var content_f23_53_24413=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F70311&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F70311&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    X-linked adrenoleukodystrophy (X-ALD) phenotypes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Phenotypes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Total ALD, percent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Symptoms/ signs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age at present- ation (years)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mis- diagnosed as",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diag- nostic test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Follow- up tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recom- mended therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"8\">",
"        Males",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asymptomatic (MRI normal)",
"       </td>",
"       <td>",
"        Increasing",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        0 to &ge;10",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        <p>",
"         VLCFA in relatives of X-ALD patients",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td>",
"        Monitor MRI and adrenal function; family screening",
"       </td>",
"       <td>",
"        Lorenzo's oil, adrenal HRT if indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asymptomatic (MRI abnormal)",
"       </td>",
"       <td>",
"        Increasing",
"       </td>",
"       <td>",
"        None (cognition normal)",
"       </td>",
"       <td>",
"        2 to &ge;10",
"       </td>",
"       <td>",
"        Other white matter disorders",
"       </td>",
"       <td>",
"        <p>",
"         VLCFA, brain MRI",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td>",
"        Neurological and neuro- psychological testing, adrenal function",
"       </td>",
"       <td>",
"        HSCT, adrenal HRT if indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Addison disease only (MRI normal)",
"       </td>",
"       <td>",
"        20 (decreases with age)",
"       </td>",
"       <td>",
"        Primary adrenocortical insufficiency, normal neurology, MRI normal",
"       </td>",
"       <td>",
"        0 to &ge;10",
"       </td>",
"       <td>",
"        Other causes of Addison disease",
"       </td>",
"       <td>",
"        <p>",
"         VLCFA",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td>",
"        Monitor MRI, neurological and neuro- psychological testing",
"       </td>",
"       <td>",
"        Lorenzo's oil, adrenal HRT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Addison disease only (MRI abnormal)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Primary adrenocortical insufficiency",
"       </td>",
"       <td>",
"        0 to &ge;10",
"       </td>",
"       <td>",
"        Other causes of Addison disease",
"       </td>",
"       <td>",
"        <p>",
"         VLCFA, brain MRI",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td>",
"        Neurological and neuro- psychological testing, MRI",
"       </td>",
"       <td>",
"        HSCT, adrenal HRT if indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Cerebral (mild) without AMN*",
"       </td>",
"       <td rowspan=\"3\">",
"        45",
"       </td>",
"       <td rowspan=\"3\">",
"        Behavior changes, school failure, dementia, audiovisual",
"       </td>",
"       <td>",
"        3-10 (common)",
"       </td>",
"       <td rowspan=\"3\">",
"        ADHD, psychological disorder, Asperger syndrome, autism",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         VLCFA, brain MRI",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td rowspan=\"3\">",
"        Neurological and neuro- psychological testing, family screening, adrenal function",
"       </td>",
"       <td rowspan=\"3\">",
"        HSCT, adrenal HRT if indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        11-21 (inter- mediate)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        21 (rare)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cerebral (severe) without AMN*",
"       </td>",
"       <td>",
"        2-3",
"       </td>",
"       <td>",
"        Dementia, psychoses, paralysis, epilepsy, loss of vision, loss of speech, bulbar palsy",
"       </td>",
"       <td>",
"        5 to adulthood",
"       </td>",
"       <td>",
"        Other neuro- degenerative diseases, brain tumor, psychosis, epilepsy",
"       </td>",
"       <td>",
"        <p>",
"         VLCFA, brain MRI",
"        </p>",
"        <p>",
"         Confirm with DNA",
"        </p>",
"       </td>",
"       <td>",
"        Adrenal function, neurological and neuro- psychological testing, family screening",
"       </td>",
"       <td>",
"        Adrenal HRT if indicated, general support",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Percentages listed above vary by age group, as the asymptomatic and Addison disease groups decline with age while the ALD and AMN groups increase.",
"    <div class=\"footnotes\">",
"     ADHD: attention-deficit/hyperactivity disorder; ALS: amyotrophic lateral sclerosis; DNA: ABCD1 mutation analysis; AMN: adrenomyeloneuropathy; EEG: electroencephalogram; HRT: hormone replacement therapy; HSCT: hematopoietic stem cell transplant; MRI: magnetic resonance imaging; MTS: magnetization transfer MRI cervical cord; SSEP: somatosensory evoked potential; VLCFA: very long chain fatty acids assay in plasma.",
"     <br>",
"      * For a description of the distinction between the mild and severe cerebral forms, see: Peters C, Charnas LR, Tan Y, et al. Cerebral X-linked adrenoleukodystrophy: the international hematopoietic cell transplantation experience from 1982 to 1999. Blood 2004;104:881.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Moser HW, Raymond GV, Dubey P. Adrenoleukodystrophy: new approaches to a neurodegenerative disease. JAMA 2005; 294:3131. Copyright &copy; 2005 American Medical Association. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
