var title_f23_56_24459="Predictors of malignancy in weight loss";
var content_f23_56_24459=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F69961&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F69961&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Predictors of malignancy among patients with unexplained involuntary weight loss",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Predictor",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Dichotomized predictor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Cut-point",
"       </td>",
"       <td class=\"subtitle2\">",
"        OR (95% CI)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age",
"       </td>",
"       <td>",
"        &gt;80 years",
"       </td>",
"       <td>",
"        3.4 (1.1-9.7)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        WBC",
"       </td>",
"       <td>",
"        &gt;12K cells/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        3.6 (1.3-10.1)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Albumin",
"       </td>",
"       <td>",
"        &lt;3.5 g/dL",
"       </td>",
"       <td>",
"        6.7 (3.3-14.3)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alkaline phosphatase",
"       </td>",
"       <td>",
"        &gt;300 U/L",
"       </td>",
"       <td>",
"        11.9 (3.9-36.2)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lactate dehydrogenase",
"       </td>",
"       <td>",
"        &gt;500 U/L",
"       </td>",
"       <td>",
"        12.5 (3.9-39.8)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Hernandez, JL, Matorras, P, Riancho, JA, Gonz&aacute;lez-Mac&iacute;as, J. Involuntary weight loss without specific symptoms: A clinical prediction score for malignant neoplasm. QJM 2003; 96:649.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
