var title_f23_56_24460="General screening ECG";
var content_f23_56_24460=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F64686&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F64686&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for a positive 12-lead ECG",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        P wave",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Left atrial enlargement: negative portion of the P wave in lead V1 &ge;0.1 mV in depth and &ge;0.04 in duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Right atrial enlargement: peaked P wave in leads II and III or V1 &ge;0.25 mV in amplitude",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        QRS complex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frontal plane axis deviation: right &ge; +120&ordm; or left -30&ordm; to -90&ordm;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased voltage: amplitude of R or S wave in a standard lead &ge;2 mV, S wave in lead V1 or V2 &ge;3 mV, or R wave in lead V5 or V6 &ge;3 mV;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal Q waves &ge;0.04 s in duration or &ge;25 percent of the height of the ensuing R wave or QS pattern in two or more leads",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Right or left bundle branch block with QRS duration &ge;0.12 s",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        R or R' wave in lead V1 &ge;0.5 mV in amplitude and R/S ratio &ge;1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        ST-segment, T-waves, and QT interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ST-segment depression or T-wave flattening or inversion in two or more leads",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolongation of heart rate corrected QT interval &gt;0.44 s in males and &gt;0.46 s in females",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rhythm and conduction abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Premature ventricular beats or more severe ventricular arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Supraventricular tachycardias, atrial flutter, or atrial fibrillation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short PR interval (&lt;0.12 s) with or without 'delta' wave",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sinus bradycardia with resting heart rate &le;40 beats/min*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        First (PR &ge;0.21 s&bull;), second or third degree atrioventricular block",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Increasing less than 100 beats/min during limited exercise test.",
"     <br>",
"      &bull; Not shortening with hyperventilation or limited exercise test.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Corrado D, Pelliccia A, Bjornstad HH, et al. Cardiovascular pre-participation screening of young competitive athletes for prevention of sudden death: proposal for a common European protocol. Consensus Statement of the Study Group of Sport Cardiology of the Working Group of Cardiac Rehabilitation and Exercise Physiology and the Working Group of Myocardial and Pericardial Diseases of the European Society of Cardiology. Eur Heart J 2005; 26:516. Copyright &copy;2005 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
