" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Beckwith-Wiedemann syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?23/57/24473/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/57/24473/contributors\">",
"     Brian Chung Hon-Yin, MD, FCCMG",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?23/57/24473/contributors\">",
