var title_f23_59_24507="Medication effects on fracture healing";
var content_f23_59_24507=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F53621&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F53621&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs that may affect fracture healing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Medication class",
"       </td>",
"       <td class=\"subtitle1\">",
"        Examples",
"       </td>",
"       <td class=\"subtitle1\">",
"        Purported effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chemotherapy",
"       </td>",
"       <td>",
"        <p>",
"         Doxorubicin",
"        </p>",
"        <p>",
"         Cyclophosphamide",
"        </p>",
"        <p>",
"         Adriamycin",
"        </p>",
"        <p>",
"         Methotrexate",
"        </p>",
"       </td>",
"       <td>",
"        Decreased callus formation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Glucocorticoids",
"       </td>",
"       <td>",
"        <p>",
"         Dexamethasone",
"        </p>",
"        <p>",
"         Prednisone",
"        </p>",
"        <p>",
"         Methylprednisolone",
"        </p>",
"       </td>",
"       <td>",
"        Evidence is inconsistent; may decrease bone mineralization",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Antibiotics",
"       </td>",
"       <td>",
"        <p>",
"         Fluoroquinolones (eg, Levofloxacin)",
"        </p>",
"        <p>",
"         Gentamicin",
"        </p>",
"        <p>",
"         Tetracycline",
"        </p>",
"       </td>",
"       <td>",
"        Fluoroquinolones have adverse effects upon cartilage regrowth and bone healing. Evidence concerning other antibiotics is inconsistent.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        NSAIDs",
"       </td>",
"       <td>",
"        Multiple nonspecific and COX-2 inhibitors",
"       </td>",
"       <td>",
"        Animal studies suggest decreased healing; human studies are inconsistent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
