var title_f23_6_23659="Guidelines for spacing live and inactivated antigens";
var content_f23_6_23659=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F59103&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F59103&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for spacing of live and inactivated antigens",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antigen combination",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommended minimum interval between doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two or more inactivated*",
"       </td>",
"       <td>",
"        May be administered simultaneously or at any interval between doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inactivated and live",
"       </td>",
"       <td>",
"        May be administered simultaneously or at any interval between doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two or more live injectable",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        28 days minimum interval, if not administered simultaneously",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Certain experts suggest a 28-day interval between tetanus toxoid, reduced diphtheria toxoid, and acellular pertussis (Tdap) vaccine and tetravalent meningococcal conjugate vaccine if they are not administered simultaneously.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Live oral vaccines (eg, Ty21a typhoid vaccine and rotavirus vaccine) may be administered simultaneously or at any interval before or after inactivated or live injectable vaccines.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Centers for Disease Control and Prevention. General Recommendations on Immunization: Recommendations of the Advisory Committee on Immunization Practices (ACIP). MMWR Recomm Rep 2011; 60(RR-2):1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
