var title_f23_60_24523="PPSV23 schedule for high-risk children older than two years";
var content_f23_60_24523=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Schedule for vaccination using 23-valent polysaccharide vaccine (PPSV23) after 13-valent pneumococcal conjugate vaccine (PCV13) for children aged &ge;2 years with underlying medical conditions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Schedule for PPSV23",
"       </td>",
"       <td class=\"subtitle1\">",
"        Revaccination with PPSV23",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children who are immunocompromised, have sickle cell disease, or functional or anatomic asplenia",
"       </td>",
"       <td>",
"        1 dose of PPSV23 administered at age &ge;2 years and &ge;8 weeks after last indicated dose of PCV13",
"       </td>",
"       <td>",
"        1 dose 5 years after the first dose of PPSV23",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunocompetent children with chronic illness*",
"       </td>",
"       <td>",
"        1 dose of PPSV23 administered at age &ge;2 years and &ge;8 weeks after last indicated dose of PCV13",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Chronic heart disease, chronic lung disease, diabetes mellitus, cerebrospinal fluid leaks, or cochlear implant.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Nuorti JP, Whitney CG, Centers for Disease Control and Prevention (CDC). Prevention of pneumococcal disease among infants and children - use of 13-valent pneumococcal conjugate vaccine and 23-valent pneumococcal polysaccharide vaccine - recommendations of the Advisory Committee on Immunization Practices (ACIP). MMWR Recomm Rep 2010; 59:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
