var title_f23_60_24525="Pegintron Rebetol dose mod";
var content_f23_60_24525=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F53213%7EGAST%2F75290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F53213%7EGAST%2F75290&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for dose modification and discontinuation of PegIntron (peginterferon) or PegIntron/REBETOL (ribavirin) for hematologic toxicity",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Laboratory values",
"       </td>",
"       <td class=\"subtitle1\">",
"        Peginterferon",
"       </td>",
"       <td class=\"subtitle1\">",
"        Ribavirin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Hemoglobin",
"       </td>",
"       <td>",
"        &lt;10 g/dL*",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Decrease by 200 mg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;8.5 g/dL",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        White blood cell count",
"       </td>",
"       <td>",
"        &lt;1.5 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Reduce dose by 50 percent",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;1 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Neutrophil count",
"       </td>",
"       <td>",
"        &lt;0.75 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Reduce dose by 50 percent",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;0.5 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Platelet count",
"       </td>",
"       <td>",
"        &lt;50 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Reduce dose by 50 percent",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;25 x 10",
"        <sup>",
"         9",
"        </sup>",
"        /L",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"       <td>",
"        Permanently discontinue",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * For patients with a history of stable cardiac disease receiving PegIntron in combination with ribavirin, the PegIntron dose should be reduced by half and the ribavirin dose by 200 mg/day if a &gt;2 g/dL decrease in hemoglobin is observed during any 4-week period. Both PegIntron and ribavirin should be permanently discontinued if patients have hemoglobin levels &lt;12 g/dL after this ribavirin dose reduction.",
"    </div>",
"    <div class=\"reference\">",
"     Table adapted from: Product information: Peg Intron&reg; (peginterferon alfa-2b). Schering Corporation, Kenilworth, NJ. Available online at: file://www.spfiles.com/pipeg-intron.pdf (Accessed on September 13, 2010).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    PEGASYS (peginterferon)/COPEGUS (ribavirin) dosage modification guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Laboratory values",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Discontinue if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        PEGASYS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ANC &ge;750/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Maintain 180 mcg",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         ANC &lt;500/mm",
"         <sup>",
"          3",
"         </sup>",
"         , treatment should be suspended until ANC values return to more than 1000/mm",
"         <sup>",
"          3",
"         </sup>",
"        </p>",
"        <p>",
"         Reinstitute at 90&nbsp;mcg and monitor ANC",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ANC &lt;750/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Reduce to 135 mcg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Platelet &ge;50,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Maintain 180 mcg",
"       </td>",
"       <td rowspan=\"2\">",
"        Platelet count &lt;25,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Platelet &lt;50,000/mm",
"        <sup>",
"         3",
"        </sup>",
"       </td>",
"       <td>",
"        Reduce to 90 mcg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        COPEGUS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemoglobin in patients with no cardiac disease",
"       </td>",
"       <td>",
"        Reduce to 600 mg/day* if &lt;10 g/dL",
"       </td>",
"       <td>",
"        &lt;8.5 g/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hemoglobin in patients with history of stable cardiac disease",
"       </td>",
"       <td>",
"        Reduce to 600 mg/day* if &ge;2 g/dL decrease in hemoglobin during any 4-week period treatment",
"       </td>",
"       <td>",
"        &lt;12 g/dL despite 4 weeks at reduced dose",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * One 200 mg tablet in the morning and two 200 mg tablets in the evening.",
"    </div>",
"    <div class=\"reference\">",
"     Table from: Pegasys (peginterferon alfa-2a) Prescribing Information and Medication Guide. Roche Pharmaceuticals, Nutley, NJ. Available online at: file://www.rocheusa.com/products/pegasys/pi.pdf (Accessed on September 4, 2007).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
