var title_f23_61_24541="Chemicals immune dysfunction";
var content_f23_61_24541=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F60126&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F60126&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Chemicals associated with immune system dysfunction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Chemical",
"      </td>",
"      <td class=\"subtitle1\">",
"       Immunologic effects",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Polycyclic aromatic hydrocarbons",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Benzopyrenes",
"      </td>",
"      <td>",
"       Reduced antibody production",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Benzanthracenes",
"      </td>",
"      <td>",
"       Reduced T cell cytotoxicity, carcinogenesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Benzene",
"      </td>",
"      <td>",
"       Lymphopenia, marrow suppression, carcinogenesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Polyhalogenated aromatic hydrocarbons",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Polychlorinated biphenyls",
"      </td>",
"      <td>",
"       Altered lymphocyte populations, decreased delayed type hypersensitivity, increased respiratory infections",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Polybrominated biphenyls",
"      </td>",
"      <td>",
"       Decreased T cell number and function",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Polychlorinated dibenzofurans",
"      </td>",
"      <td>",
"       Depressed cellular immunity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Aromatic amines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Benzidine",
"      </td>",
"      <td>",
"       Depressed cellular immunity, bladder carcinomas",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Estrogenic xenobiotics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       DDT",
"      </td>",
"      <td>",
"       Thymic atrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Zearalenol",
"      </td>",
"      <td>",
"       Myelotoxicity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Tetrahydrocannabinol",
"      </td>",
"      <td>",
"       Depressed cellular immunity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Diethylstilbestrol",
"      </td>",
"      <td>",
"       Reduced natural killer activity, reduced antibody synthesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Pesticides",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Organochlorines (including DDT)",
"      </td>",
"      <td>",
"       Lymphopenia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Organophosphates",
"      </td>",
"      <td>",
"       Thymic atrophy, reduced T cell cytotoxicity, reduced antibody synthesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Methyl isocyanate",
"      </td>",
"      <td>",
"       Increased CD4+ T cells, decreased mitogen response",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Organometals",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Organotins",
"      </td>",
"      <td>",
"       Impaired generation of CTL thymic atrophy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Methylmercury",
"      </td>",
"      <td>",
"       Inhibition of complement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Dimethylnitrosamine",
"      </td>",
"      <td>",
"       Reduced antibody production, carcinogenic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Heavy metals",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Arsenic, cadmium, copper, gold, iron, lead, zinc",
"      </td>",
"      <td>",
"       Bone marrow suppression, lymphopenia, carcinogenesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Asbestos",
"      </td>",
"      <td>",
"       Reduced NK function, reduced CD4/CD8 ratio, cytotoxic to alveolar macrophages, depressed CMI, polyclonal B cell activation, autoimmunity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Silica production",
"      </td>",
"      <td>",
"       Decreased antibody, prolonged allograft rejection, cytotoxic to alveolar macrophages, depressed CMI, polyclonal B cell activation, autoimmunity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Air pollutants",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ozone",
"      </td>",
"      <td>",
"       Decrease resistance to experimental infections",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Sulfur dioxide",
"      </td>",
"      <td>",
"       Respiratory tract infections",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Nitrogen dioxide",
"      </td>",
"      <td>",
"       Decreased alveolar macrophage phagocytosis and bacterial capacity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Fungal toxins",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ochratoxin",
"      </td>",
"      <td>",
"       Depressed antibody production",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"1\" rowspan=\"2\">",
"       Trichothecenes",
"      </td>",
"      <td>",
"       Decreased T and B cell mitogen responses, leukopenia, delayed skin graft rejection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Inhibition of macrophage activation, hepatic carcinoma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ethanol",
"      </td>",
"      <td>",
"       Reduced T cell function, reduced macrophage function, reduced antibody production, impaired neutrophil chemataxis, increased respiratory tract infection",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
