var title_f23_7_23675="Evaluation of infants with WNV";
var content_f23_7_23675=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F58635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F58635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommended clinical evaluation of infants with clinical or laboratory evidence of possible congenital West Nile virus (WNV) infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Computerized tomography (CT) scan of the head and",
"brain. If abnormal, a pediatric neurologist should be consulted.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pediatric ophthalmologic evaluation, including",
"examination of the retina.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Complete blood count, platelet count, and liver",
"function tests, including alanine aminotransferase (ALT) and aspartate",
"aminotransferase (AST). Examination of the CSF should be considered,",
"and if done, should include testing of the CSF for IgM to WNV.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evaluation by a dysmorphologist or clinical geneticist.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Further evaluation of any congenital abnormalities to",
"determine alternative causes, including genetic, infectious, or other",
"teratogenic causes.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Repeat hearing screen at age 6 months.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Careful evaluation of head circumference, physical",
"characteristics, and developmental milestones throughout the first year",
"of life.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Repeat examination of infant serum for IgG and IgM",
"antibody to WNV at age 6 months.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histopathologic examination of the placenta and",
"umbilical cord, testing of frozen placental tissue and cord tissue for",
"WNV nucleic acid, and testing of cord serum for IgM and IgG antibody to",
"WNV.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     www.cdc.gov.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
