" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   NSAIDs: Acute kidney injury (acute renal failure) and nephrotic syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/0/24582/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/0/24582/contributors\">",
"     Burton D Rose, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/0/24582/contributors\">",
"     Theodore W Post, MD",
"    </a>",
"    <br/>",
"   </div>",
