" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Approach to the patient with eosinophilia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/10/24745/contributors\">",
