" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Incidence of some inborn errors of metabolism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Biotinidase deficiency",
"       </td>",
"       <td>",
"        1 in 72,000 to 1 in 126,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital adrenal hyperplasia",
"       </td>",
"       <td>",
"        1 in 12,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital hypothyroidism",
"       </td>",
"       <td>",
"        1 in 3,600 to 1 in 5,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cystic fibrosis",
"       </td>",
"       <td>",
"        1 in 2,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Galactosemia",
"       </td>",
"       <td>",
"        1 in 60,000 to 1 in 80,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Homocystinuria",
"       </td>",
"       <td>",
"        1 in 50,000 to 1 in 150,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Maple syrup urine disease",
"       </td>",
"       <td>",
"        1 in 250,000 to 1 in 400,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phenylketonuria",
"       </td>",
"       <td>",
"        1 in 10,000 to 1 in 25,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sickle cell disease",
"       </td>",
"       <td>",
"        1 in 2,800",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Toxoplasmosis",
"       </td>",
"       <td>",
"        1 in 1,000 to 1 in 8,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tyrosinemia",
"       </td>",
"       <td>",
"        1 in 19,000",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
