var title_f24_13_24795="Diagnostic criteria for rheumatic heart disease";
var content_f24_13_24795=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F66581&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F66581&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for rheumatic heart disease based on clinical and echocardiographic findings",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category of rheumatic heart disease",
"       </td>",
"       <td class=\"subtitle1\">",
"        <p>",
"         Finding on clinical cardiac auscultation (ie, presence of murmur of MR, AR, and/or MS)",
"        </p>",
"        <p>",
"         OR",
"        </p>",
"        <p>",
"         Echocardiographic structural and functional changes of MS without murmur (ie, \"silent\" mitral stenosis)",
"        </p>",
"       </td>",
"       <td class=\"subtitle1\">",
"        Echocardiographic valvular structural abnormality (ie, presence of typical rheumatic valve morphology)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Echocardiographic functional abnormality (ie, presence of significant valve regurgitation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Definite",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Probable",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Possible",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        -",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Source: WHO Technical Report Series. Rheumatic fever and rheumatic heart disease: Report of a WHO expert panel, Geneva 29 October - 1 November 2001. Geneva: WHO; 2004.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
