var title_f24_13_24796="Suicide risk assessment1";
var content_f24_13_24796=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54465&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54465&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suicide risk assessment for children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Content and nature of suicidal ideation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        What are the thoughts?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are they active (volitional and with intent) or passive/non-volitional?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How often do they occur?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have they increased in intensity or frequency?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How long do they last (duration)? Are they obsessive?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Can the thoughts be controlled?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How powerful, intense is the urge?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How do the thoughts make you feel?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Anticipated method/Planning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the child or adolescent have a method in mind? If so, have the adolescent describe it",
"        <strong>",
"         explicitly",
"        </strong>",
"        (since this provides information about the adolescent's level of intent and planning)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Sample questions include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Do you have a plan?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        What time would you do it?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Where would you do it?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Do you think it would work?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Do you think this would kill you?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Is there anyone who could find you and save you?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Access to means",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the adolescent have",
"        <strong>",
"         access",
"        </strong>",
"        to the method they have in mind?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        What is the level of dangerousness or lethality of the means they are considering?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the child or adolescent have access to firearms and medications?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Sample questions include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Do you have any of these things (pills, gun, razor, etc) in your possession?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        How easy would it be for you to get them?",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Jacobs, DG, Brewer, M, Klein-Benheim, M. Suicide assessment: An overview and recommended protocol. In: Harvard Medical School Guide to Assessment and Intervention in Suicide, Jacob, D (Ed). Jossey-Bass, San Francisco 1998. p.3.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
