var title_f24_14_24812="Treatment of hyperkalemia in children";
var content_f24_14_24812=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F82874&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F82874&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of hyperkalemia in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onset of action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Stabilization of cardiac myocardial membrane in setting of hyperkalemia-associated abnormal ECG or arrhythmia&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Calcium gluconate, 10 percent*",
"       </td>",
"       <td>",
"        <p>",
"         0.5 to 1 mL/kg IV over 5 to 15 min (50 to&nbsp;100 mg/kg calcium gluconate, maxiumum dose 3 grams)",
"        </p>",
"        <p>",
"         Repeat after 10 minutes, if needed",
"        </p>",
"       </td>",
"       <td>",
"        Immediate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Movement of extracellular potassium into the cells&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glucose and insulin",
"       </td>",
"       <td>",
"        <p>",
"         IV administration of glucose 0.5 g/kg&nbsp;(equal to&nbsp;2 mL/kg of a 25 percent dextrose solution)&nbsp;",
"        </p>",
"        <p>",
"         IV administration of insulin 0.1 units/kg over 30 min",
"        </p>",
"       </td>",
"       <td>",
"        30 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inhaled beta-agonists (albuterol)",
"       </td>",
"       <td>",
"        0.1 to 0.3 mg/kg",
"       </td>",
"       <td>",
"        30 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sodium bicarbonate&bull;",
"       </td>",
"       <td>",
"        IV administration of 1 mEq/kg over 10 min (maximum dose of 50 mEq per hour)",
"       </td>",
"       <td>",
"        15 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Removal of potassium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sodium polystyrene sulfonate",
"       </td>",
"       <td>",
"        1 g/kg PO or PR with sorbitol",
"       </td>",
"       <td>",
"        1 to 2 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;Furosemide",
"       </td>",
"       <td>",
"        IV administration of 1 to 2 mg/kg with replacement of fluid loss",
"       </td>",
"       <td>",
"        1 to 2 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &nbsp;Hemodialysis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IV: intravenous administration, PO: oral administration, PR: rectal administration, ECG: electrocardiogram.",
"     <br>",
"      <br>",
"       *Calcium should be administered in a larger vein or central line preferably (irritant), and sodium bicarbonate should not be introduced into the line because of potential precipitation. Continuous ECG monitoring should be performed during administration.",
"       <br>",
"        &bull;Administration of sodium bicarbonate may have a minimal effect.",
"        <br>",
"         &Delta;These measures do not remove potassium and additional interventions may be required to lower&nbsp;overall potassium levels.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
