" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Rectovaginal or bowel endometriosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/17/24858/contributors\">",
