var title_f24_18_24876="Testing in rapidly progressive dementia";
var content_f24_18_24876=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F55420&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F55420&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rapidly progressive dementia: Potential diagnostic testing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        In all patients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brain MRI, including FLAIR and DWI, with and without gadolinium enhancement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum electrolytes, liver, renal, and thyroid function tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin B12, homocysteine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinalysis, culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        In most patients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lumbar puncture, cell count and differential, protein, glucose, syphilis serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        EEG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        In selected patients (based on abnormalities in above tests or suggestive clinical features):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        CSF",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cryptococcal antigen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bacterial fungal AFB stains and cultures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cytology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Viral PCRs and cultures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lyme serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        14-3-3 protein",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Blood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Rheumatological screen (ESR, ANA, CRP)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Antithyroglobulin, anti-thyroperoxidase antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        HIV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lyme",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Paraneoplastic antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Copper, ceruloplasmin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Whipples PCR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine copper, heavy metal screen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Further imaging (angiography, PET, SPECT)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brain biopsy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MRI: magnetic resonance imaging; FLAIR: fluid-attenuated inversion recovery; DWI: diffusion weighted imaging; PET: positron emission tomography; SPECT: single photon emission computed tomography; ESR: erythrocyte sedimentation rate; ANA: antinuclear antibodies; CRP: C-reactive protein.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f24_18_24876=[""].join("\n");
