var title_f24_18_24877="Isolation precautions system";
var content_f24_18_24877=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F50001&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F50001&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    System of isolation precautions for infection control",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of precaution",
"       </td>",
"       <td class=\"subtitle1\">",
"        Selected patients",
"       </td>",
"       <td class=\"subtitle1\">",
"        Major specifications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Standard",
"       </td>",
"       <td>",
"        All patients",
"       </td>",
"       <td>",
"        <p>",
"         Handwashing before and after every patient contact*",
"        </p>",
"        <p>",
"         Gloves, gowns, eye protection as required",
"        </p>",
"        <p>",
"         Safe disposal or cleaning of instruments and linen",
"        </p>",
"        <p>",
"         Cough etiquette: Patients and visitors should cover their nose or mouth when coughing, promptly dispose used tissues and practice hand hygiene after contact with respiratory secretions",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Contact",
"       </td>",
"       <td>",
"        <p>",
"         Colonization of any bodily site with multidrug-resistant bacteria (MRSA, VRE)",
"        </p>",
"        <p>",
"         Enteric infections (",
"         <em>",
"          C. difficile",
"         </em>",
"         ,",
"         <em>",
"          E. coli",
"         </em>",
"         O157:H7, viral infections (RSV, HSV, enterovirus, parainfluenza))",
"        </p>",
"        <p>",
"         Scabies",
"        </p>",
"        <p>",
"         Impetigo",
"        </p>",
"        <p>",
"         Noncontained abcesses or decubitus ulcers (especially for",
"         <em>",
"          Staphylococcus aureus",
"         </em>",
"         and group A streptococcus)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          In addition to standard precautions:",
"         </strong>",
"        </p>",
"        <p>",
"         Wash hands with soap and water before and after leaving the patient's room",
"        </p>",
"        <p>",
"         Private room preferred; cohorting allowed if necessary",
"        </p>",
"        <p>",
"         Gloves required upon entering room. Change gloves after contact with contaminated secretions.",
"        </p>",
"        <p>",
"         Gown required if clothing may come into contact with the patient or environmental surfaces, or if the patient has diarrhea",
"        </p>",
"        <p>",
"         Minimize risk of environmental contamination during patient transport (eg, patient can be placed in a gown)",
"        </p>",
"        <p>",
"         Noncritical items should be dedicated to use for a single patient if possible",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Droplet",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Known or suspected:",
"         </strong>",
"        </p>",
"        <p>",
"         <em>",
"          Neisseria meningitidis",
"         </em>",
"        </p>",
"        <p>",
"         <em>",
"          Haemophilus influenzae",
"         </em>",
"         type B",
"        </p>",
"        <p>",
"         <em>",
"          Mycoplasma pneumoniae",
"         </em>",
"        </p>",
"        <p>",
"         <em>",
"          Bordetella pertussis",
"         </em>",
"        </p>",
"        <p>",
"         Diphtheria",
"        </p>",
"        <p>",
"         Pneumonic plague",
"        </p>",
"        <p>",
"         Influenza",
"        </p>",
"        <p>",
"         Rubella",
"        </p>",
"        <p>",
"         Mumps",
"        </p>",
"        <p>",
"         Adenovirus",
"        </p>",
"        <p>",
"         Parvovirus B19",
"        </p>",
"        <p>",
"         RSV",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          In addition to standard precautions:",
"         </strong>",
"        </p>",
"        <p>",
"         Private room preferred; cohorting allowed if necessary",
"        </p>",
"        <p>",
"         Wear a mask when within 3 feet of the patient",
"        </p>",
"        <p>",
"         Mask the patient during transport",
"        </p>",
"        <p>",
"         Cough etiquette: Patients and visitors should cover their nose or mouth when coughing, promptly dispose used tissues and practice hand hygiene after contact with respiratory secretions",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Airborne",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Known or suspected:",
"         </strong>",
"        </p>",
"        <p>",
"         Tuberculosis",
"        </p>",
"        <p>",
"         Varicella",
"        </p>",
"        <p>",
"         Measles",
"        </p>",
"        <p>",
"         Smallpox",
"        </p>",
"        <p>",
"         SARS",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          In addition to standard precautions:",
"         </strong>",
"        </p>",
"        <p>",
"         Place the patient in an AIIR (airborne infection isolation room), [a monitored negative pressure room with at least 6-12 air exchanges per hour]",
"        </p>",
"        <p>",
"         Room exhaust must be appropriately discharged outdoors or passed through a HEPA (high-efficiency particulate aerator) filter before recirculation within the hospital",
"        </p>",
"        <p>",
"         A certified respirator must be worn when entering the room of a patient with diagnosed or suspected tuberculosis. Susceptible individuals should not enter the room of patients with confirmed or suspected measles or chickenpox.",
"        </p>",
"        <p>",
"         Transport of the patient should be minimized; the patient should be masked if transport within the hospital is unavoidable",
"        </p>",
"        <p>",
"         Cough etiquette: Patients and visitors should cover their nose or mouth when coughing, promptly dispose used tissues and practice hand hygiene after contact with respiratory secretions",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This system of isolation precautions is recommended by the United States Healthcare Infection Control Practices Advisory Committee.",
"    <div class=\"footnotes\">",
"     * Alcohol-based hand disinfectant is an acceptable alternative to soap and water in all situations EXCEPT in the setting of",
"     <em>",
"      Clostridium difficile",
"     </em>",
"     , for which soap and water should be used.",
"    </div>",
"    <div class=\"reference\">",
"     Modified from Garner JS. Infect Control Hosp Epidemiol 1996; 17:53.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f24_18_24877=[""].join("\n");
