" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of peripheral lymphadenopathy*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cause",
"       </td>",
"       <td class=\"subtitle1\">",
"        Examples",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        Bacterial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Localized",
"       </td>",
"       <td class=\"sublist_other\">",
"        Streptococcal pharyngitis; skin infections; tularemia; plague; cat scratch disease; diphtheria; chancroid; rat bite fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Generalized",
"       </td>",
"       <td class=\"sublist_other\">",
"        Brucellosis; leptospirosis; lymphogranuloma venereum; typhoid fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Viral",
"       </td>",
"       <td>",
"        Human immunodeficiency virus; Epstein-Barr virus; herpes simplex virus; cytomegalovirus; mumps; measles; rubella; hepatitis B; dengue fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycobacterial",
"       </td>",
"       <td>",
"        <em>",
"         Mycobacterium tuberculosis",
"        </em>",
"        ; atypical mycobacteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fungal",
"       </td>",
"       <td>",
"        Histoplasmosis; coccidioidomycosis; cryptococcosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Protozoal",
"       </td>",
"       <td>",
"        Toxoplasmosis; Leishmaniasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spirochetal",
"       </td>",
"       <td>",
"        Secondary syphilis; Lyme disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Cancer",
"       </td>",
"       <td>",
"        Squamous cell cancer head and neck; metastatic; lymphoma; leukemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" rowspan=\"4\">",
"        Lymphoproliferative",
"       </td>",
"       <td>",
"        Angioimmunoblastic lymphadenopathy with dysproteinemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autoimmune lymphoproliferative disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rosai-Dorfman disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemophagocytic lymphohistiocytosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Immunologic",
"       </td>",
"       <td>",
"        Serum sickness; drug reactions (phenytoin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Endocrine",
"       </td>",
"       <td>",
"        Hypothyroidism; Addison's disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Miscellaneous",
"       </td>",
"       <td>",
"        Sarcoidosis; lipid storage diseases; amyloidosis; histiocytosis; chronic granulomatous diseases; Castleman's disease; Kikuchi's disease; Kawasaki disease; inflammatory pseudotumor; systemic lupus erythematosus; rheumatoid arthritis; Still's disease; dermatomyositis; Churg-Strauss syndrome",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
