var title_f24_20_24907="Diet for pediatric hypertriglyceridemia";
var content_f24_20_24907=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F61553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F61553&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diet for pediatric hypertriglyceridemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Refer to a registered dietitian for family medical nutrition therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Dietary components include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          25 to 30 percent of calories from fat, &le;7 percent from saturated fat, ~10 percent from monounsaturated fat; &lt;200 mg/d of cholesterol; avoid trans fats as much as possible.",
"         </li>",
"         <li>",
"          Decrease sugar intake, replace simple with complex carbohydrates, and no sugar sweetened beverages.",
"         </li>",
"         <li>",
"          Increase dietary fish to increase omega-3 fatty acid.&nbsp;&nbsp;Environmental Protection Agency is advising women of childbearing age who may become pregnant, pregnant women,&nbsp;nursing mothers, and young children to avoid some types of fish and shellfish and eat fish and shellfish that are low in mercury. For more information, call the United States Food and Drug Administration's FDA) food information line toll free at 1-888-SAFEFOOD or visit file://www.cfsan.fda.gov/~dms/admehg3.",
"         </li>",
"         <li>",
"          If child is obese, nutrition therapy should include calorie restriction and increased activity.",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Daniels SR, Benuck I, Christakis DA, et al. Expert panel on integrated guidelines for cardiovascular health and risk reduction in children and adolescents: Full report, 2011. National Heart Lung and Blood Institute. Available at:",
"     <a href=\"file://www.nhlbi.nih.gov/guidelines/cvd_ped/peds_guidelines_full.pdf\" target=\"_blank\">",
"      file://www.nhlbi.nih.gov/guidelines/cvd_ped/peds_guidelines_full.pdf",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
