var title_f24_21_24923="Classification of BOS";
var content_f24_21_24923=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F70127&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F70127&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of bronchiolitis obliterans syndrome after lung transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pulmonary function criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BOS 0",
"       </td>",
"       <td>",
"        FEV1 &gt;90 percent baseline",
"        <strong>",
"         and",
"        </strong>",
"        FEF 25-75 that is &gt;75 percent of baseline value*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BOS 0-p (potential)",
"       </td>",
"       <td>",
"        FEV1 81 to 90 percent baseline",
"        <strong>",
"         and/or",
"        </strong>",
"        FEF 25-75 that is &le;75 percent of baseline value*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BOS 1",
"       </td>",
"       <td>",
"        FEV1 66 to 80 percent of baseline value*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BOS 2",
"       </td>",
"       <td>",
"        FEV1 51 to 65 percent of baseline value*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        BOS 3",
"       </td>",
"       <td>",
"        FEV1 &le;50 percent of baseline value*",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *Baseline defined as the average of the best two FEV1 values after transplant. Decline in FEV1 is determined by two measurements at least one month apart. FEF 25-75 is the forced expiratory flow rate achieved between 25 and 75 percent of the total expiratory time.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Estenne M, Maurer JR, Bohler A, et al. Bronchiolitis obliterans syndrome 2001: An update of the diagnostic criteria. J Heart Lung Transplant 2002; 21:297.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
