var title_f24_22_24939="Diagnostic criteria for acute and recurrent pericarditis";
var content_f24_22_24939=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F64302&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F64302&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for acute and recurrent pericarditis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Acute pericarditis (at least 2 criteria of 4):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Typical pleuritic chest pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pericardial friction rub",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Suggestive electrocardiogram changes (typically widespread ST segment elevation, PR depression)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New or worsening pericardial effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recurrent pericarditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Typical pleuritic chest pain plus at least one objective evidence of disease activity:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fever",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pericardial rub",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ECG changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New or worsening pericardial effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Elevation of markers of inflammation (elevation in white blood cell count, erythrocyte sedimentation rate, or C-reactive protein)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
