var title_f24_22_24940="Criteria schwannomatosis";
var content_f24_22_24940=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F57973&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F57973&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for the diagnosis of schwannomatosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria for definite schwannomatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        A. Age &gt;30 years and all of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Two or more nonintradermal schwannomas, at least one with histologic confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No evidence of vestibular tumor on high-quality MRI scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No known constitutional NF2 mutation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. One pathologically confirmed nonvestibular schwannoma plus a first-degree relative who meets above criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria for possible schwannomatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        A. Age &lt;30 years and all of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Two or more nonintradermal schwannomas, at least one with histologic confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No evidence of vestibular tumor on high quality MRI scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No known constitutional NF2 mutation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        B. Age &gt;45 years and all of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Two or more nonintradermal schwannomas, at least one with histologic confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No symptoms of 8th cranial nerve dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; No known constitutional NF2 mutation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Radiographic evidence of a nonvestibular schwannoma and first degree relative meeting criteria for definite schwannomatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria for segmental schwannomatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meets criteria for either definite or possible schwannomatosis and schwannomas limited to one limb or &le;5 contiguous spine segments",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: MacCollin M, Chiocca EA, Evans DG, et al. Diagnostic criteria for schwannomatosis. Neurology 2005; 64:1838. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
