" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Lipid abnormalities after cardiac transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/26/25000/contributors\">",
"     Author",
"    </a>",
"    <br/>",
