" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   T cell receptor signaling",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/26/25001/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/26/25001/contributors\">",
"     Chaim M Roifman, MD, FRCPC, FCACB",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/26/25001/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
