var script_f24_28_25025=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function FractExcSodium_SI_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.FractExcSodium_SI_form){",
"",
"",
"doCalc = true;",
"if (Urine_Na_param.value.indexOf(',') >= 0){ Urine_Na_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Urine_Na_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Urine_Na_unit.options[Urine_Na_unit.selectedIndex].value.split('|');",
"Urine_Na = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_Na_param.value.indexOf(',') >= 0){ Serum_Na_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Na_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Na_unit.options[Serum_Na_unit.selectedIndex].value.split('|');",
"Serum_Na = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Urine_Cr_param.value.indexOf(',') >= 0){ Urine_Cr_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Urine_Cr_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Urine_Cr_unit.options[Urine_Cr_unit.selectedIndex].value.split('|');",
"Urine_Cr = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Serum_Cr_param.value.indexOf(',') >= 0){ Serum_Cr_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Serum_Cr_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Serum_Cr_unit.options[Serum_Cr_unit.selectedIndex].value.split('|');",
"Serum_Cr = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"FE_Na =  (Urine_Na / Serum_Na) / (Urine_Cr / Serum_Cr) * 100;",
"",
"unit_parts = FE_Na_unit.options[FE_Na_unit.selectedIndex].value.split('|');",
"if (doCalc) FE_Na_param.value = fixDP((FE_Na - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.FractExcSodium_SI_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.FractExcSodium_SI_form){",
"",
"FE_Na_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
