"  <div id=\"topicText\">",
"   <p>",
"    (For additional information",
"    <a class=\"drug drug_general\" href=\"UTD.htm?38/31/39417?source=see_link\">",
"     see \"Metoprolol: Drug information\"",
"    </a>",
"    and",
"    <a class=\"drug drug_pediatric\" href=\"UTD.htm?15/11/15544?source=see_link\">",
"     see \"Metoprolol: Pediatric drug information\"",
"    </a>",
"    )",
"   </p>",
"   <div class=\"list ubnlist drugH1Div drugBrandNames\" id=\"F195796\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
