var title_f24_28_25035="Montreal classification of severity of ulcerative colitis";
var content_f24_28_25035=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F86284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F86284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Montreal classification of severity of ulcerative colitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Severity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S0",
"       </td>",
"       <td>",
"        Clinical remission",
"       </td>",
"       <td>",
"        Asymptomatic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S1",
"       </td>",
"       <td>",
"        Mild UC",
"       </td>",
"       <td>",
"        Passage of four or fewer stools/day (with or without blood), absence of any systemic illness, and normal inflammatory markers (ESR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S2",
"       </td>",
"       <td>",
"        Moderate UC",
"       </td>",
"       <td>",
"        Passage of more than four stools per day but with minimal signs of systemic toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        S3",
"       </td>",
"       <td>",
"        Severe UC",
"       </td>",
"       <td>",
"        Passage of at least six bloody stools daily, pulse rate of at least 90 beats per minute, temperature of at least 37.5&deg;C, haemoglobin of less than 10.5 g/dL, and ESR of at least 30 mm/hour",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     UC: ulcerative colitis; ESR: erythrocyte sedimentation rate.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Gut, Satsangi J, Silverberg MS, Vermere S, Colombel JF. The Montreal classification of inflammatory bowel disease: controversies, consensus, and implications, 55:749-53, copyright &copy; 2006, with permission from BMJ Publishing Group Ltd.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
