var title_f24_28_25036="Complications corticosteroid Rx";
var content_f24_28_25036=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F71400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F71400&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Complications of corticosteroid therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neurologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Behavioral changes, insomnia, myopathy, hallucinations, hiccups, tremor, reduced taste and smell, cerebral atrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Psychosis, dementia, seizures, dependence, paraparesis (epidural lipomatosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Dermatologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thin, fragile skin, purpura, echymosis, striae, hirsuitism, acne, inhibition of wound healing, Kaposi's sarcoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rheumatologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osteoporosis, avascular necrosis, growth retardation, tendinous rupture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gastrointestinal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased appetite, abdominal bloating, gastrointestinal bleeding and perforation, pancreatitis, liver hypertrophy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Opthalmologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visual blurring, cataract, glaucoma, exophthalmos, uveitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cardiovascular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypertension, atherosclerosis, arrhythmia (with IV push)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Endocrine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperglycemia, hypokalemia, hypophosphatemia, hypernatremia, hyperlipidemia, redistribution of body fat (centripetal obesity, buffalo hump, etc), amenorrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Urogenital",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polyuria, genital burning (with IV push)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Miscellaneous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Opportunistic infections (including candidiasis, PCP), hypersensitivity reactions, neutrophilia, lymphopenia, night sweats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Steroid withdrawal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pseudorheumatism (very common), headache, lethargy, low-grade fever, adrenal insufficiency, pseudotumor cerebri",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data adapted from Delattre, JY, Posner, JB, Neurological complications of chemotherapy and radiation therapy. In: Neurology and General Medicine, Aminoff MJ, (Ed), Churchill Livingstone, New York, 1994. p.421-446.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
