var title_f24_32_25100="Pulm tox neoplastic agents";
var content_f24_32_25100=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F60543&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F60543&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical syndromes of pulmonary toxicity caused by antineoplastic agents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Syndromes",
"      </td>",
"      <td class=\"subtitle1\">",
"       Description",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute bronchoconstriction",
"      </td>",
"      <td>",
"       Evidence of airflow limitation (eg, wheezing, prolonged expiratory phase, reduced FEV1)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Infusion reaction",
"      </td>",
"      <td>",
"       <p>",
"        Acute onset of symptoms/signs (eg, angioedema, flushing, itching, urticaria, arthralgias, bronchoconstriction, dyspnea, hypotension, hypoxemia, nausea, or back pain) during or shortly after infusion",
"       </p>",
"       <p>",
"        May be caused by mast cell/basophil activation or cytokine release in a systemic inflammatory reaction",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Alveolar hemorrhage",
"      </td>",
"      <td>",
"       Dyspnea, sometimes hemoptysis, diffuse radiographic opacities, hypoxemia, BAL fluid with evidence of hemorrhage",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Eosinophilic pneumonia",
"      </td>",
"      <td>",
"       Dyspnea, diffuse radiographic pulmonary opacities, hypoxemia, and BAL fluid eosinophilia (&gt;20 percent eosinophils), sometimes with peripheral eosinophilia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hypersensitivity pneumonitis",
"      </td>",
"      <td>",
"       <p>",
"        Dyspnea and radiographic pulmonary opacities developing within hours to days of treatment due to a cell-mediated (Type IV) delayed hypersensitivity reaction",
"       </p>",
"       <p>",
"        May be associated with pulmonary eosinophilia",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Interstitial pneumonitis (includes nonspecific interstitial pneumonia, usual interstitial pneumonia, and organizing pneumonia)",
"      </td>",
"      <td>",
"       Dyspnea, cough, sometimes fever, diffuse or focal radiographic opacities (eg, septal thickening, ground glass opacities, consolidation) and nonspecific BAL findings",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Radiation recall lung injury",
"      </td>",
"      <td>",
"       <p>",
"        Appearance of radiographic opacities in areas of prior irradiation in response to treatment with a cytotoxic agent",
"       </p>",
"       <p>",
"        Often associated with cough and dyspnea",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Noncardiogenic pulmonary edema",
"      </td>",
"      <td>",
"       Pulmonary edema without evidence of heart failure or increase in left atrial pressure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Capillary leak syndrome",
"      </td>",
"      <td>",
"       Noncardiogenic pulmonary edema associated with diffuse peripheral edema and sometimes intravascular hypovolemia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute lung injury/ARDS",
"      </td>",
"      <td>",
"       Noncardiogenic pulmonary edema PLUS evidence of acute inflammation (eg, fever, elevated neutrophils in BAL fluid)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FEV1: forced expiratory volume in one second; BAL: bronchoalveolar lavage; ARDS: acute respiratory distress syndrome.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
