var title_f24_32_25101="ADHD drugs comparison";
var content_f24_32_25101=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F80151&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F80151&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of drugs used to treat attention deficit hyperactivity disorder in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Short-acting stimulants",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Extensive data efficacy and safety",
"        </p>",
"        Generic formulations available",
"       </td>",
"       <td>",
"        Must be taken 2 to 3 times per day if full-day coverage is desired",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Long-acting stimulants",
"        </strong>",
"       </td>",
"       <td>",
"        Can be dosed once per day (can avoid administration at school)",
"       </td>",
"       <td>",
"        <p>",
"         Side effects may extend later in the day",
"        </p>",
"        <p>",
"         Increased cost",
"        </p>",
"        Fewer generic options",
"       </td>",
"       <td>",
"        Short-acting stimulant can be added in PM for afternoon boost when needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Single-pulse sustained release (eg, Ritalin SR&reg;, Metadate ER&reg;, Methylin ER&reg;)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Must be swallowed whole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sustained release bead preparations (eg, Dexedrine&reg; Spansule&reg;, Ritalin LA&reg;, Focalin XR&reg;, Adderall XR&reg;, Metadate CD&reg;)",
"       </td>",
"       <td>",
"        <p>",
"         Approximates a twice-per-day dosing schedule",
"        </p>",
"        Can be sprinkled into soft foods for children who have difficulty swallowing pills",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         The beads should not be chewed",
"        </p>",
"        <p>",
"         Should not be taken with antacids or other drugs that decrease gastric acidity",
"        </p>",
"        Ingestion with a high-fat meal may delay time of onset and increase peak concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Osmotic release (eg, Concerta&reg;)",
"       </td>",
"       <td>",
"        Approximates a three-times-per-day dosing schedule",
"       </td>",
"       <td>",
"        Children with decreased GI absorption or intestinal resection may not receive the full benefit",
"       </td>",
"       <td>",
"        <p>",
"         Capsule should not be opened or chewed",
"        </p>",
"        Capsule is passed through the GI tract and into the stool intact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patch (Daytrana&reg;)",
"       </td>",
"       <td>",
"        Can be used for patients who cannot take oral medications",
"       </td>",
"       <td>",
"        Patch must be applied 2 hours before needed effect",
"       </td>",
"       <td>",
"        Early removal of the patch permits controlled duration (effects last approximately 2 to 3 hours after patch is removed)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Prodrug (Lisdexamfetamine)",
"       </td>",
"       <td>",
"        <p>",
"         May have lower risk for abuse",
"        </p>",
"        Capsules can be opened and mixed with water for children who have difficulty swallowing capsules",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Atomoxetine",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Can be dosed once per day",
"        </p>",
"        <p>",
"         Lower potential for abuse than stimulants",
"        </p>",
"        Not a controlled substance",
"       </td>",
"       <td>",
"        <p>",
"         May be less efficacious than stimulants",
"        </p>",
"        <p>",
"         Potential increased risk of suicidal ideation",
"        </p>",
"        <p>",
"         More time to steady state (up to 2 weeks for initial response; up to 8 weeks for maximal effect)",
"        </p>",
"        \"Drug holidays\" are not an option",
"       </td>",
"       <td>",
"        <p>",
"         Taking with food may prevent nausea",
"        </p>",
"        Requires dosing adjustment if administered with agents that inhibit the cytochrome P450 2D6 (CYP2D6) enzyme",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Extended release alpha-2-adrenergic agonists",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Not a controlled substance",
"        </p>",
"        May also treat coexisting conditions (eg, sleep disorders, tic disorders)",
"       </td>",
"       <td>",
"        <p>",
"         May be less efficacious than stimulants",
"        </p>",
"        <p>",
"         More time to steady state (up to 2 weeks for initial response)",
"        </p>",
"        \"Drug holidays\" are not an option",
"       </td>",
"       <td>",
"        <p>",
"         Must be swallowed whole, not crushed or chewed",
"        </p>",
"        May lead to hypotension and orthostasis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     GI: gastrointestinal.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Daughton JM, Kratochvil CJ. Review of ADHD pharmacotherapies: Advantages, disadvantages, and clinical pearls. J Am Acad Child Adolesc Psychiatry 2009; 48:240.",
"      </li>",
"      <li>",
"       Harpin VA. Medication options when treating children and adolescents with ADHD: interpreting the NICE guidance 2006. Arch Dis Child Educ Pract Ed 2008; 93:58.",
"      </li>",
"      <li>",
"       Drugs for treatment of ADHD. Treat Guidel Med Lett 2006; 4:77.",
"      </li>",
"      <li>",
"       Wigal SB. Efficacy and safety limitations of attention deficit hyperactivity disorder pharmacotherapy in pediatric patients. J Pediatr 2009; 154:S13.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
