" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of hematopoiesis and stem cell function",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/34/25129/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/34/25129/contributors\">",
"     Colin A Sieff, MB, BCh, FRCPath",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
