"   <a href=\"?imageKey=ID%2F65985%7EID%2F50115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F65985%7EID%2F50115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
