" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Nonselective NSAIDs: Adverse cardiovascular effects",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/4/24648/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/4/24648/contributors\">",
"     Daniel H Solomon, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?24/4/24648/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/4/24648/contributors\">",
"     Daniel E Furst, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?24/4/24648/contributors\">",
"     Christopher P Cannon, MD",
"    </a>",
"    <br/>",
"   </div>",
