var title_f24_4_24651="Luxation injuries";
var content_f24_4_24651=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54063&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54063&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of luxation injuries",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Luxation injury",
"      </td>",
"      <td class=\"subtitle1\">",
"       Description",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Concussion",
"      </td>",
"      <td>",
"       The tooth is neither loose nor displaced; it may be tender with the pressure of biting because of inflammation of the periodontal ligament",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Subluxation",
"      </td>",
"      <td>",
"       The tooth is loose but not displaced from its socket; the periodontal ligament fibers are damaged and inflamed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intrusion",
"      </td>",
"      <td>",
"       The tooth is driven into the socket, compressing the periodontal ligament and fracturing the alveolar socket",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Extrusion",
"      </td>",
"      <td>",
"       The tooth is centrally dislocated from its socket; the periodontal ligament is lacerated and inflamed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lateral luxation",
"      </td>",
"      <td>",
"       The tooth is displaced labially, lingually, or laterally; the periodontal ligament is lacerated, and the supporting bone is fractured",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avulsion",
"      </td>",
"      <td>",
"       The tooth is completely displaced from the alveolar ridge; the periodontal ligament is severed, and fracture of the alveolus may occur",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Andreasen, J, Andreasen, F. Textbook and Color Atlas of Traumatic Injuries to the Teeth, 3rd ed, Munksgaard, Copenhagen 1994.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
