var title_f24_40_25227="Detection methods for abnormal hemoglobins";
var content_f24_40_25227=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F57389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F57389&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Testing protocol for detection of hemoglobinopathies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Clinical evaluation (anemia, polycythemia, cyanosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CBC, reticulocyte count, peripheral smear, Heinz body prep",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electrophoresis on cellulose acetate &plusmn; electropheresis on citrate agar,",
"        <strong>",
"         or",
"        </strong>",
"        isoelectric focusing on agarose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        HPLC methods to separate and identify mutant Hb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Cation exchange HPLC (Hb quantitation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Reversed phase HPLC (globin chain separation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Special tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Isopropanol, heat stability (unstable Hbs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; Oxygen affinity (p50)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        DNA-based methods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; PCR amplification and sequencing of globin genes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        &bull; PCR detection of deletions",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
